{% extends "base.html" %}

{% block title %}NastavenÃ­ - WebDAV Uploader{% endblock %}

{% block content %}
<div class="d-flex justify-content-between flex-wrap flex-md-nowrap align-items-center pt-3 pb-2 mb-3 border-bottom">
    <h1 class="h2">NastavenÃ­ systÃ©mu</h1>
</div>

<div class="row">
    <div class="col-md-8">
        <div class="card">
            <div class="card-header">
                <h5><i class="bi bi-gear"></i> WebDAV konfigurace</h5>
            </div>
            <div class="card-body">
                <form method="POST" id="settingsForm">
                    <div class="mb-3">
                        <label for="webdav_hostname" class="form-label">WebDAV Server</label>
                        <input type="url" class="form-control" id="webdav_hostname" 
                               value="{{ config.webdav_config.webdav_hostname if config.webdav_config else '' }}" 
                               placeholder="https://your-webdav-server.com">
                    </div>

                    <div class="row">
                        <div class="col-md-6">
                            <div class="mb-3">
                                <label for="webdav_login" class="form-label">UÅ¾ivatelskÃ© jmÃ©no</label>
                                <input type="text" class="form-control" id="webdav_login" 
                                       value="{{ config.webdav_config.webdav_login if config.webdav_config else '' }}">
                            </div>
                        </div>
                        <div class="col-md-6">
                            <div class="mb-3">
                                <label for="webdav_password" class="form-label">Heslo</label>
                                <input type="password" class="form-control" id="webdav_password" 
                                       placeholder="â€¢â€¢â€¢â€¢â€¢â€¢â€¢â€¢">
                            </div>
                        </div>
                    </div>

                    <div class="mb-3">
                        <label for="webdav_root" class="form-label">VÃ½chozÃ­ sloÅ¾ka</label>
                        <input type="text" class="form-control" id="webdav_root" 
                               value="{{ config.webdav_config.webdav_root if config.webdav_config else '' }}" 
                               placeholder="/uploads/">
                    </div>

                    <div class="mb-3">
                        <label for="max_file_size" class="form-label">MaximÃ¡lnÃ­ velikost souboru (MB)</label>
                        <input type="number" class="form-control" id="max_file_size" 
                               value="{{ (config.max_file_size / 1024 / 1024) | round if config.max_file_size else 50 }}">
                    </div>

                    <button type="submit" class="btn btn-primary">
                        <i class="bi bi-save"></i> UloÅ¾it nastavenÃ­
                    </button>

                    <button type="button" class="btn btn-outline-secondary" onclick="testConnection()">
                        <i class="bi bi-wifi"></i> Test pÅ™ipojenÃ­
                    </button>
                </form>
            </div>
        </div>

        <div class="card mt-4">
            <div class="card-header">
                <h5><i class="bi bi-shield-check"></i> BezpeÄnostnÃ­ nastavenÃ­</h5>
            </div>
            <div class="card-body">
                <form>
                    <div class="mb-3">
                        <label for="max_login_attempts" class="form-label">Max. pokusÅ¯ o pÅ™ihlÃ¡Å¡enÃ­</label>
                        <input type="number" class="form-control" id="max_login_attempts" value="5" min="1" max="10">
                    </div>

                    <div class="mb-3">
                        <label for="lockout_duration" class="form-label">Doba uzamÄenÃ­ (minuty)</label>
                        <input type="number" class="form-control" id="lockout_duration" value="30" min="5" max="1440">
                    </div>

                    <div class="mb-3">
                        <label for="session_timeout" class="form-label">Timeout relace (hodiny)</label>
                        <input type="number" class="form-control" id="session_timeout" value="24" min="1" max="168">
                    </div>

                    <div class="form-check mb-3">
                        <input class="form-check-input" type="checkbox" id="enable_logs" checked>
                        <label class="form-check-label" for="enable_logs">
                            Povolit logovÃ¡nÃ­ pÅ™Ã­stupÅ¯
                        </label>
                    </div>

                    <button type="submit" class="btn btn-warning">
                        <i class="bi bi-shield-check"></i> UloÅ¾it bezpeÄnostnÃ­ nastavenÃ­
                    </button>
                </form>
            </div>
        </div>
    </div>

    <div class="col-md-4">
        <div class="card">
            <div class="card-header">
                <h5><i class="bi bi-info-circle"></i> AktuÃ¡lnÃ­ konfigurace</h5>
            </div>
            <div class="card-body">
                {% if config %}
                <dl class="row">
                    <dt class="col-sm-6">WebDAV Server:</dt>
                    <dd class="col-sm-6">
                        {% if config.webdav_config %}
                            <small>{{ config.webdav_config.webdav_hostname }}</small>
                        {% else %}
                            <span class="text-muted">Nenastaveno</span>
                        {% endif %}
                    </dd>

                    <dt class="col-sm-6">Max. velikost:</dt>
                    <dd class="col-sm-6">
                        {% if config.max_file_size %}
                            {{ (config.max_file_size / 1024 / 1024) | round }} MB
                        {% else %}
                            <span class="text-muted">N/A</span>
                        {% endif %}
                    </dd>

                    <dt class="col-sm-6">PovolenÃ© typy:</dt>
                    <dd class="col-sm-6">
                        {% if config.allowed_extensions %}
                            <small>{{ config.allowed_extensions | length }} typÅ¯</small>
                        {% else %}
                            <span class="text-muted">N/A</span>
                        {% endif %}
                    </dd>
                </dl>
                {% else %}
                <div class="alert alert-warning">
                    <i class="bi bi-exclamation-triangle"></i>
                    Nelze naÄÃ­st konfiguraci API serveru
                </div>
                {% endif %}
            </div>
        </div>

        <div class="card mt-3">
            <div class="card-header">
                <h5><i class="bi bi-file-earmark-text"></i> PovolenÃ© typy souborÅ¯</h5>
            </div>
            <div class="card-body">
                {% if config.allowed_extensions %}
                <div class="row">
                    {% for ext in config.allowed_extensions %}
                    <div class="col-6 col-lg-4 mb-2">
                        <span class="badge bg-light text-dark">.{{ ext }}</span>
                    </div>
                    {% endfor %}
                </div>
                {% else %}
                <p class="text-muted">Informace o povolenÃ½ch typech nejsou dostupnÃ©</p>
                {% endif %}
            </div>
        </div>

        <div class="card mt-3">
            <div class="card-header">
                <h5><i class="bi bi-tools"></i> SystÃ©movÃ© akce</h5>
            </div>
            <div class="card-body">
                <div class="d-grid gap-2">
                    <button type="button" class="btn btn-outline-warning" onclick="restartAPI()">
                        <i class="bi bi-arrow-clockwise"></i> Restart API serveru
                    </button>
                    <button type="button" class="btn btn-outline-danger" onclick="clearLogs()">
                        <i class="bi bi-trash"></i> Vymazat logy
                    </button>
