    // TODO: Implementovat vytvoÅ™enÃ­ uÅ¾ivatele
    alert(`âœ… UÅ¾ivatel bude vytvoÅ™en:\nJmÃ©no: ${username}\nEmail: ${email}\nAdmin: ${isAdmin ? 'Ano' : 'Ne'}`);

    // ZavÅ™enÃ­ modalu
    let modal = bootstrap.Modal.getInstance(document.getElementById('addUserModal'));
